def self.record_comment(ts, te, meta)
token = [
:COMMENT,
meta[:line],
meta[:col],
meta[:data][ts, te - ts].pack(PACK_DIRECTIVE).force_encoding(UTF_8_ENCODING),
meta[:previous_token],
]

meta[:previous_token] = token

meta[:col] += te - ts
end

def self.emit(token_name, ts, te, meta, token_value = nil)
token_value ||= meta[:data][ts, te - ts].pack(PACK_DIRECTIVE).force_encoding(UTF_8_ENCODING)
meta[:tokens] << token = [
token_name,
meta[:line],
meta[:col],
token_value,
meta[:previous_token],
]
meta[:previous_token] = token
# Bump the column counter for the next token
meta[:col] += te - ts
end

ESCAPES = /\\["\\\/bfnrt]/
ESCAPES_REPLACE = {
'\\"' => '"',
"\\\\" => "\\",
"\\/" => '/',
"\\b" => "\b",
"\\f" => "\f",
"\\n" => "\n",
"\\r" => "\r",
"\\t" => "\t",
}

UTF_8 = /\\u(?:([\dAa-f]{4})|\{([\da-f]{4,})\})(?:\\u([\dAa-f]{4}))?/i


VALID_STRING = /\A(?:[^\\]|#{ESCAPES}|#{UTF_8})*\z/o

PACK_DIRECTIVE = "c*"
UTF_8_ENCODING = "UTF-8"

def self.emit_string(ts, te, meta, block:)
quotes_length = block ? 3 : 1
value = meta[:data][ts + quotes_length, te - ts - 2 * quotes_length].pack(PACK_DIRECTIVE).force_encoding(UTF_8_ENCODING) || ''
line_incr = 0
if block && !value.empty?
line_incr = value.count("\n")
value = GraphQL::Language::BlockString.trim_whitespace(value)
end

if !value.valid_encoding? || !value.match?(VALID_STRING)
meta[:tokens] << token = [
:BAD_UNICODE_ESCAPE,
meta[:line],
meta[:col],
value,
meta[:previous_token],
]
else
replace_escaped_characters_in_place(value)

if !value.valid_encoding?
	meta[:tokens] << token = [
	:BAD_UNICODE_ESCAPE,
	meta[:line],
	meta[:col],
	value,
	meta[:previous_token],
	]
	else
	meta[:tokens] << token = [
	:STRING,
	meta[:line],
	meta[:col],
	value,
	meta[:previous_token],
	]
	end
end

meta[:previous_token] = token
meta[:col] += te - ts
meta[:line] += line_incr
end
